mapper.select:
SELECT 

FROM t_product_receive_detail a

WHERE a.id = #{id}

SELECT 

FROM t_product_receive_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.product_receive_id = #{productReceiveId}


		AND a.material_code = #{materialCode}


		AND a.material_name = #{materialName}


		AND a.material_model = #{materialModel}


		AND a.material_spec = #{materialSpec}


		AND a.unit = #{unit}


		AND a.amount = #{amount}


		AND a.receiver_id = #{receiverId}


		AND a.receiver_name = #{receiverName}


		AND a.qualified_amount = #{qualifiedAmount}


		AND a.unqualified_amount = #{unqualifiedAmount}


		AND a.unqualified_introduction = #{unqualifiedIntroduction}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_product_receive_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO t_product_receive_detail( id, product_receive_id, material_code, material_name, material_model, material_spec, unit, amount, receiver_id, receiver_name, receive_time, qualified_amount, unqualified_amount, unqualified_introduction, create_by, create_date, update_by, update_date, del_flag, receive_introduction , bar_code , material_type_id, material_type_name
) VALUES ( #{id}, #{productReceiveId}, #{materialCode}, #{materialName}, #{materialModel}, #{materialSpec}, #{unit}, #{amount}, #{receiverId}, #{receiverName}, #{receiveTime}, #{qualifiedAmount}, #{unqualifiedAmount}, #{unqualifiedIntroduction}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{delFlag}, #{receiveIntroduction}, #{barCode}, #{materialTypeId}, #{materialTypeName}
)

mapper.update:
UPDATE t_product_receive_detail SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE t_product_receive_detail SET 
	del_flag = '1'
WHERE product_receive_id = #{receiptBillId}

mapper.select:
select d.id as receiptDetailId, d.receiver_id as checkerId, d.receiver_name as checkerName, d.receive_time as checkDate, d.amount as arrivalAmount, d.qualified_amount as qualifiedAmount, d.unqualified_amount as unqualifiedAmount, d.unqualified_introduction as unqualifiedDesc, d.receive_introduction as introduction, d.material_code as materialCode, d.material_name as materialName, d.material_model as modelType, d.material_spec as specification, d.unit as unitName, d.material_type_id as materialCategoryCode, d.material_type_name as materialCategoryName, d.bar_code as barCode from t_product_receive_detail d
d.del_flag ='0'
AND d.product_receive_id = #{receiptBillId}
AND d.receiver_id = #{userId}
